def menu():
    global selmenu, ppass, wait, growing, transition, up, x5, returned
    showtext()
    x5 = 16
    while True:
        cupyminimenu()
        if thumby.buttonB.pressed() and up == False and selmenu == 0:
            transition = True
            growing = False
            up = True
        elif thumby.buttonB.pressed() and selmenu == 1:
            thumby.display.setFont("/lib/font5x7.bin", 5, 7, 1)
            return 0
        if not thumby.buttonB.pressed() and up == True:
            up = False
        if not thumby.buttonU.pressed() and not thumby.buttonD.pressed():
            ppass = True
        if thumby.buttonD.pressed() and ppass == True:
            selmenu += 1
            ppass = False
        if thumby.buttonU.pressed() and ppass == True:
            selmenu -= 1
            ppass = False
        if selmenu < 0:
            selmenu = 1
        if selmenu > 1:
            selmenu = 0
        if transition == True:
            holemoving()
        if x5 < 0:
            tothegame()
        if returned == True:
            holemoving()
        if x5 == 17:
            returned = False
            x5 = 16
            showtext()
        if transition == False:
            thumby.display.drawText(">", 9, 20 + selmenu*8, 1)
            thumby.display.drawText(">", 9, 28 - selmenu*8, 0)
        thumby.display.update()
