def waitingclock():
    global wait, x, x4
    while wait >= 0:
        x += 1
        if x <= 30:
            clockloading1()
        elif x <= 60:
            clockloading2()
        elif x <= 105:
            clockloading3()
        else:
            x = 0
        wait -= 1
        thumby.display.update()
    x = 0
    x4 = 0
    x5 = 0
